Week-3 Programing Assignment-02:
 #include &lt;stdio.h&gt;


int is_prime(int n) {
    if (n <= 1) return 0;
    for (int j = 2; j * j <= n; j++) {
        if (n % j == 0) return 0;
    }
    return 1;
}

int main() {
    int n, num;
    scanf("%d", &n);

    int flag = 0;

    for (int in = 0; in < n; in++) {
        scanf("%d", &num);

        if (is_prime(num)) {
            if (flag == 0) {
                printf("%d", num);
                flag = 1;     
            } else {
                printf(" %d", num);
            }
        }
    }

    return 0;
}

