 #include &lt;stdio.h&gt;

int main() {
    int arraySize, searchValue, positionFound = -1;
    int numberArray[20]; // limit as per constraint
    int loopIndex;

    // Read the size of the array
    scanf("%d", &arraySize);

    // Read array elements
    for (loopIndex = 0; loopIndex < arraySize; loopIndex++) {
        scanf("%d", &numberArray[loopIndex]);
    }

    // Read the search value
    scanf("%d", &searchValue);

    // Search for first occurrence
    for (loopIndex = 0; loopIndex < arraySize; loopIndex++) {
        if (numberArray[loopIndex] == searchValue) {
            positionFound = loopIndex;
            break;
        }
    }

    // Output the result
    printf("%d", positionFound);

    return 0;
}

