Week-4: Programing Assignment-03:
 #include &lt;stdio.h&gt;

int main() {
    int arraySize;
    char firstString[21], secondString[21]; // +1 for null terminator
    int letterCount1[26] = {0}, letterCount2[26] = {0};
    int loopIndex, isAnagram = 1;

    // Read size of character arrays
    scanf("%d", &arraySize);

    // Read first string
    scanf("%s", firstString);

    // Read second string
    scanf("%s", secondString);

    // Count frequency of each letter in first string
    for (loopIndex = 0; loopIndex < arraySize; loopIndex++) {
        letterCount1[firstString[loopIndex] - 'A']++;
    }

    // Count frequency of each letter in second string
    for (loopIndex = 0; loopIndex < arraySize; loopIndex++) {
        letterCount2[secondString[loopIndex] - 'A']++;
    }

    // Compare frequency counts
    for (loopIndex = 0; loopIndex < 26; loopIndex++) {
        if (letterCount1[loopIndex] != letterCount2[loopIndex]) {
            isAnagram = 0;
            break;
        }
    }

    // Output result
    printf("%d", isAnagram);

    return 0;
}

