    // Method to return the largest number in the array
    public int getMax() {
        int max = numbers[0]; // Assume first element is the largest
        for (int num : numbers) {
            if (num > max) {
                max = num;
            }
        }
        return max;
    }

    // Method to return the smallest number in the  ans
    public int getMin() {
        int min = numbers[0]; // Assume first element is the smallest
        for (int num : numbers) {
            if (num < min) {
                min = num;
            }
        }
        return min;
    }
}

