class B implements GCD {
    int n1,n2;

    //Create a method to calculate GCD ok
    public int findGCD(int n1, int n2){
		if(n1 == 0 && n2 == 0) {
			return -1;
		}
		else if(n2 == 0){
			return n1;
		}

		else {
			return findGCD(n2, n1%n2);
		}
        }
 }

