double result = 0;
char operator = ' ';
String firsty = "", secondd = "";

// Split the input string into character array
char[] arr = input.toCharArray();

// Separate two operands and operator
for (char ch : arr) {
    if (ch == '+' || ch == '-' || ch == '*' || ch == '/') {
        operator = ch;
    } else {
        if (operator == ' ') {
            firsty += ch;  // before operator
        } else {
            secondd += ch; // after operator
        }
    }
}

// Convert operands into integers
int op1 = Integer.parseInt(firsty.trim());
int op2 = Integer.parseInt(secondd.trim());

// Perform the required operation
switch (operator) {
    case '+':
        result = op1 + op2;
        break;
    case '-':
        result = op1 - op2;
        break;
    case '*':
        result = op1 * op2;
        break;
    case '/':
        if (op2 != 0) {
            result = (double) op1 / op2;
        } else {
            System.out.println("Division by zero is not allowed");
            return;
        }
        break;
    default:
        System.out.println("Invalid operator");
        return;
}

// Print the output as stated in the question
System.out.print(op1 + "" + operator + "" + op2 + " = " + Math.round(result));

