 class Point {
    private double x;
    private double y;

    // Constructor
    public Point(double x, double y) {
        this.x = x;
        this.y = y;
    }

    // Method to calculate distance between two points
    public double distance(Point p2) {
        double dxxx = this.x - p2.x;
        double dy = this.y - p2.y;
        return Math.sqrt(dxxx * dxxx + dy * dy);
    }
}

