	static void flipflop(char[][] flip){
		// Flip-Flop Operation
		for(int i=0; i < 5;i++){
			for(int j=0; j < 5;j++){		
				if(flip[i][j]=='1')
					flip[i][j]='0';
				else
					flip[i][j]='1';
			}
		}

